import React from 'react';
import Title from '../Title/Title';
import errow from '../../assets/arrowRight.svg';
import { Link } from 'react-router-dom';
import RotateText from '../RotateText/RotateText.component';

const Hero = () => {
   return (
      <div className='hero container'>
         <div className='hero__title flex--center'>
            <Title headingStyle='bolder--800' headingSize='heading--h1'>
               <h1 className='nav-links translate-cursor'>
                  Website and<br/>user interface<br/> designer
                  <Link to='/about'>

                     <img
                        className='hero__arrow nav-links' 
                        src={errow}
                        alt='hero arrow'
                        />
                  </Link>
               </h1>
            </Title>
         </div>
         <div className='hero__rotate'>
            <RotateText/>
         </div>
      </div>
   );
};

export default Hero;
