import React, { useRef } from 'react';

import { handleServesReturn, handleServes } from './Animation';

import Title from '../Title/Title';
import photography from '../../assets/images/photography.jpg';
import typographie from '../../assets/images/typographie.jpg';
import coding from '../../assets/images/coding.jpg';

const serves = [
   {
      name: 'Photography',
      image: photography,
   },
//    {
//       name: 'Typographie',
//       image: typographie,
//    },
//    {
//       name: 'Coding',
//       image: coding,
//    },
];

const Section = () => {
   let servesBackground = useRef(null);

   return (
      <div className='service_container'>
         <div
            className='service_background container'
            ref={(el) => {
               servesBackground = el;
            }}
         ></div>

            <div className='service_'>
               {serves.map((el) => (
                  <span
                    //  className='container'
                     key={el.name}
                     onMouseEnter={() =>
                        handleServes(el.image, servesBackground)
                     }
                     onMouseOut={() => handleServesReturn(servesBackground)}
                  >
                     <h1 className='serves1'>{el.name}</h1>
                  </span>
               ))}
            </div>

      </div>
   );
};

export default Section;
