import React from 'react';
import Title from '../Title/Title';
import errow from '../../assets/arrowRight.svg';

const Hero = () => {
   return (
      <div className='hero container'>
         <div className='hero__title'>
            <Title headingStyle='bolder--800' headingSize='heading--h1'>
               {' '}
               <h1>
                  Website and user interface designer.{' '}
                  <img className='hero__arrow' src={errow} />
               </h1>
            </Title>


           </div>
           <div className='hero__rotate'>
               <svg className='rotate-center' viewBox='0 0 20 200' width='300' height='300'>
                  <defs>
                     <path
                        id='circle'
                        d='
        M 75, 75
        m -37, 0
        a 37,37 0 1,1 74,0
        a 37,37 0 1,1 -74,0'
                       />
                  </defs>
                   <text

   letter-spacing= '3'

                       font-size='12' >
                       <textPath href='#circle'>GHOFRANE BEN HMAID - 2020 -</textPath>
                  </text>
               </svg>
            </div>
      </div>
   );
};

export default Hero;
