import React, { useState } from "react";
import emailjs from 'emailjs-com'
const ContactForm = () => {
  const [status, setStatus] = useState("Submit");

  // const handleSubmit = async (e) => {
  //   e.preventDefault();
  //   setStatus("Sending...");
  //   const { name, email, message } = e.target.elements;
  //   let details = {
  //     name: name.value,
  //     email: email.value,
  //     message: message.value,
  //   };
  //   let response = await fetch("http://localhost:3000/contact", {
  //     method: "POST",
  //     headers: {
  //       "Content-Type": "application/json;charset=utf-8",
  //     },
  //     body: JSON.stringify(details),
  //   });
  //   setStatus("Submit");
  //   let result = await response.json();
  //   alert(result.status);
  // };
  const handleSubmit = (e) =>
  {
  e.preventDefault();

    emailjs.sendForm('gmail', 'template_f9ebbhh', e.target, 'user_aXqQTrtBsF9IAVe3ZE6kQ')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
    e.target.reset();
}

  return (
    <form className='contact' onSubmit={handleSubmit}>
      <div className='input'>
        <label className='input__label' htmlFor="name">Name:</label>
        <input className='input__name' type="text" id="name" name='name' required />
      </div>
      <div className='input'>
        <label className='input__label' htmlFor="name">Name:</label>
        <input className='input__name' type="text" id="name" name='subject' required />
      </div>
      <div className='input'>
        <label className='input__label' htmlFor="email">Email:</label>
        <input className='input__email' type="email" id="email" name='email' required />
      </div>
      <div className='input'>
        <label className='input__label' htmlFor="message">Message:</label>
        <textarea className='input__message'  id="message" name='message' required />
      </div>
      <button type="submit">{status}</button>
    </form>
  );
};

export default ContactForm;
