import React from 'react';
import Button from '../Button/Button';
import Title from '../Title/Title';
const Card = (props) => {
   const { image, title, discription, goto } = props;
   return (
      <div className='card'>
         <div className='card__img'>
            <img src={`${image}`} alt={title} />
         </div>
         <div className='card__text'>
            <div>
               <Title headingStyle='bolder--800' headingSize='heading--h3'>
                  {title}
               </Title>
            </div>
            <div>
               {' '}
               {!!discription && (
                  <Title
                     headingStyle='normal--400'
                     headingSize='heading--small'
                  >
                     {discription}
                  </Title>
               )}
            </div>

            <a href={`${goto}`}></a>

            <Button
               type='button'
               buttonStyle='btn--primary--outlign'
               buttonSize='btn--small'
            >
               See WebSite
            </Button>
         </div>
      </div>
   );
};

export default Card;
