import React, { useState, useEffect } from 'react';
import Title from '../Title/Title';


const Slider = ({ slides }) => {
   const [current, setCurrent] = useState(0);
   const [active,setActive] = useState(0);
   const { length } = slides;



 useEffect(() => {

     setTimeout( ()=> setCurrent((current + 1) % slides.length), 3000);

 }, []);

   const handleSetClick = event => 
   {
      setCurrent(slides[event.target.getAttribute('data-slides')])
      setCurrent(event.target.getAttribute('data-slides'))
   }

   // const goToNext = () => {
   //    setCurrent(current === length - 1 ? 0 : current + 1);
   // };


   // useEffect(() => {
   //     setTimeout(goToNext, 3000);
   //    return function () {
   //       clearTimeout(goToNext);
   //    };
   // });

   if (!Array.isArray(slides) || length <= 0) return null;

   return (
      <section className='slider flex'>
         {slides.map((s, i) => (
            <div
               // if active slide, include the "active" class
               className={i === current ? 'slide active' : 'slide'}
               key={s.title}
               // if not active, hide from screen readers for accessibility
               aria-hidden={i !== current}
            >
               <div>
                  <div className='text--mb--s'>
                     <Title
                        headingStyle='bolder--800'
                        headingSize='heading--h2'
                     >
                        {s.title}
                     </Title>
                  </div>
                  <div>

                  <Title
                     headingStyle='normal--400'
                     headingSize='heading--small'
                     >
                     {s.subtitle}
                  </Title>
                  </div>

                  { Object.keys(current).map(index => (
                     <span 
                        onClick={ event => handleSetClick(event) }
                        data-slides={ index }
                        key={index}
                     />
                        ))}


                 </div>

            </div>
         ))}
      </section>
   );
};

export default Slider;
