import ReactDOM from 'react-dom'
import Card from '../Card/Card.component';

import dataCard from '../../assets/data/data.json'

const [loading, setLoading] = useState(true);

  const [data, setData] = useState([]);

useEffect( async () => {
    setLoading(true);
    const response = await fetch(dataCard);
    const data = response.json();
    const [item] = data.results;

    setData(item);
        setLoading(false);
          console.log(data);


}, []);

const Hero = () => {
    return (
        <div className='hero'>
            <Card>
                {loading && 'Loading...'}
                { (!loading && data.length === 0) && 'Sorry, nothing found.' }
                {}
            </Card>
        </div>
    )
}

export default Hero
