import React, { useEffect, useRef, useState } from 'react';
import Title from '../components/Title/Title'
import { Expo } from 'gsap';

import profileImage from '../assets/images/ghofranebh.png';
import useMousePosition from '../assets/useMousePosition';


const About = () =>
{


     let list = useRef();
   const [hoverState, setHoverState] = useState(false);
   const { x, y } = useMousePosition();
   const [listPosition, setListPosition] = useState({
      top: 0,
      left: 0,
   });


   useEffect(() => {
      setListPosition({
         top: list.current.getBoundingClientRect().top,
         left: list.current.getBoundingClientRect().left,
      });
   }, []);
    return (
        <div className=' about' ref={list}>
            <div
            onHoverStart={() => setHoverState(true)}
                onHoverEnd={ () => setHoverState(false) }
                className
            >

                <Title headingStyle='bolder--800' headingSize='heading--h1'> Design - Illustratur</Title>
            </div>
            <div
             className='floating-image'
            initial={{ opacity: 0 }}
            animate={{
               opacity: hoverState ? 1 : 0,
               x: x - listPosition.left,
               y: y - listPosition.top,
            }}
            transition={{
               // ease: Power3.easeOut,
               ease: Expo.easeOut,
               duration: 0.8,
            }}
            >
            <img src={profileImage} alt='profileImage' />

            </div>
        </div>
    )
}

export default About
