import React, { useRef } from 'react';
import Title from '../Title/Title';
import gsap from 'gsap';

import photography from '../../assets/images/photography.jpg';
import typographie from '../../assets/images/typographie.jpg';
import coding from '../../assets/images/coding.jpg';

const serves = [
   {
      name: 'Photography',
      image: photography,
   },
   {
      name: 'Typographie',
      image: typographie,
   },
   {
      name: 'Coding',
      image: coding,
   },
];

const Section = () => {
   let servesBackground = useRef(null);

   const handleServes = (serves) => {
      gsap.to(servesBackground, {
         duration: 0,
         background: `url(${serves}) center center`,
      });
      gsap.to(servesBackground, {
         duration: 0.4,
         opacity: 0,
         ease: 'power3.inOut',
      });
      gsap.from(servesBackground, {
         duration: 0.4,
         skeyY: 2,
         transformrigin: 'right top',
      });
   };

   const handleServesReturn = () => {
      gsap.to(servesBackground, {
         duration: 0.4,
         opacity: 0,
      });
   };
   return (
      <div className='hero container'>
         <div
            className='serves-background'
            ref={(el) => {
               servesBackground = el;
            }}
         ></div>
           <Title headingStyle='bolder--800' headingSize='heading--h1'>
               <div className>
{serves.map((el) => (
               <span
                  key={el.name}
                  onMouseEnter={() => handleServes(el.image, servesBackground)}
                  onMouseOut={() => handleServesReturn(servesBackground)}
               >
                  {el.name}
               </span>
         ))}
               </div>

            </Title>
      </div>
   );
};

export default Section;
