import React from 'react';

const STYLES = [
   'btn--primary--outline',
   'btn--warning--outline',
   'btn--danger--outline',
   'btn--success--outline',
   'btn--simple',
   'btn--cercle',
   'btn--primary--solid',
   'btn--warning--solid',
   'btn--danger--solid',
   'btn--success--solid',
   'btn--solid'
];

const SIZES = ['btn--cercle','btn--simple', 'btn--small', 'btn--medium', 'btn--large'];
const Button = ({ children, type, onClick, buttonStyle, buttonSize }) => {
   const checkButtonStyle = STYLES.includes(buttonStyle)
      ? buttonStyle
      : STYLES[0];
   const checkButtonSize = SIZES.includes(buttonSize) ? buttonSize : SIZES[0];
   return (
      <button
         className={`btn ${checkButtonStyle} ${checkButtonSize}`}
         onClick={onClick}
         type={type}
      >
         {children}
      </button>
   );
};

export default Button;
