import React, {useEffect,useState} from 'react';

import Card from '../Card/Card.component';
import treasure from '../../assets/images/treasureHunt.png'

const Hero = () =>
{
     const [isLoading, setIsLoading] = useState(false);
    const [tiles, setTiles] = useState();


    useEffect(() =>
    {(async () =>
    {
        setIsLoading(true)
            const response = await fetch('https://bitbucket.org/ghofranebenhmaid/ghofrane.dev/raw/91832b79902169b4dd878b33425a4394df91a26f/src/assets/data/data.json');
        const {titles: jsonResponse} = await response.json();

            console.log(jsonResponse);
        setTiles(jsonResponse)
        setIsLoading(false)

        })();

    }, [])
    return (
        <div className='hero'> 


            {isLoading && 'Loading...' }
            {(!isLoading && titles.length === 0) && 'Sorry, nothing found.'}
            {(titles.length > 0) &&
                titles.map(((data, index) => (
                     (<Card
                        key={ index }

                        image={ data.image }
                        title={ data.title }
                        description={ data.description }
                        goto="https://github.com/ghofranebenhmaid/fp-class12"
                    />)
                )


                    )) }
        </div>
    )
}

export default Hero
