import React, { useState,useRef, useEffect } from 'react';
import { withRouter, Link } from 'react-router-dom';
import Hamburger from './Hamburger';
import './Header.style.scss';
import { HiMenuAlt3, HiOutlineX } from "react-icons/hi";

import { gsap } from 'gsap';

import {

   staggerReveal,
   handleHover,
   handleHoverExit,
   staggerRevealClose,
} from './Animations';

const Navbar = ({ history,state2 }) =>
{
   let menuLayer = useRef(null);
   let reveal1 = useRef(null);
   let reveal2 = useRef(null);

   useEffect(() => {
      // If the menu is open and we click the menu button to close it.
      if (state2.clicked === false) {
         // If menu is closed and we want to open it.

         staggerRevealClose(reveal2, reveal1);
         // Set menu to display none
         gsap.to(menuLayer, { duration: 1, css: { display: 'none' } });
      } else if (
         state2.clicked === true ||
         (state2.clicked === true && state2.initial === null)
      ) {
         // Set menu to display block
         gsap.to(menuLayer, { duration: 0, css: { display: 'block' } });
         //Allow menu to have height of 100%
         gsap.to([reveal1, reveal2], {
            duration: 0,
            opacity: 1,
            height: '100%',
            // width: '100%',
         });
         staggerReveal(reveal1, reveal2);
         // fadeInUp(info);
         // staggerText(line1, line2, line3, line4, line5);
      }
   }, [state2]);





   // State of our Menu
   const [st, setState] = useState({
      initial: false,
      clicked: null,
      menuName: <HiMenuAlt3 /> ,
   });
   // State of our button
   const [disabled, setDisabled] = useState(false);

   //Use Effect
   useEffect(() => {
      //Listening for page changes.
      history.listen(() => {
         setState({ clicked: false, menuName: <HiMenuAlt3 />  });
      });
   }, [history]);

   // Toggle menu
   const handleMenu = () => {
      disableMenu();
      if (st.initial === false) {
         setState({
            initial: null,
            clicked: true,
            menuName: <HiOutlineX/>,
         });
      } else if (st.clicked === true) {
         setState({
            clicked: !st.clicked,
            menuName: <HiMenuAlt3/> ,
         });
      } else if (st.clicked === false) {
         setState({
            clicked: !st.clicked,
            menuName: <HiOutlineX/>,
         });
      }
   };

   //Determine if out menu button should be disabled
   const disableMenu = () => {
      setDisabled(!disabled);
      setTimeout(() => {
         setDisabled(false);
      }, 1200);
   };

   return (
      <header>
         <div className='container'>
            <div className='wrapper'>
               <div className='inner-header'>
                  <div
            ref={(el) => (reveal1 = el)}
            className='menu-secondary-background-color'
         ></div>
                  <div className='logo'
                     ref={ (el) => (reveal2 = el) }
                     onMouseEnter={(e) => handleHover(e)}
                                 onMouseOut={(e) => handleHoverExit(e)}
                  >
                     <Link to='/'>GHOFRANE.</Link>
                  </div>

                  <div className='menu'>

                     <button
                        className='menu--btn'
                        disabled={disabled}
                        onClick={handleMenu}
                     >
                        {st.menuName}
                     </button>
                  </div>
               </div>
            </div>
         </div>
         <Hamburger st={st} />
      </header>
   );
};

export default withRouter(Navbar);
