import React, { useState, useEffect } from 'react';
import Title from '../Title/Title';

const Slider = ({ slides }) => {
   const [curr, setCurr] = useState(0);
   const { length } = slides;

   const goToNext = () => {
      setCurr(curr === length - 1 ? 0 : curr + 1);
   };

   useEffect(() => {
      setTimeout(goToNext, 5000);
      return function () {
         clearTimeout(goToNext);
      };
   });
   if (!Array.isArray(slides) || length <= 0) return null;

   return (
      <section className='slider flex'>
         {slides.map((s, i) => (
            <div
               // if active slide, include the "active" class
               className={i === curr ? 'slide active' : 'slide'}
               key={s.title}
               // if not active, hide from screen readers for accessibility
               aria-hidden={i !== curr}
            >
               <div>
                  <div className='text--mb--s'>
                     <Title
                        headingStyle='bolder--800'
                        headingSize='heading--h2'
                     >
                        {s.title}
                     </Title>
                  </div>
                  <Title
                     headingStyle='normal--400'
                     headingSize='heading--small'
                  >
                     {s.subtitle}
                  </Title>
                 <div > {`${{...curr}}`} </div>
                 </div>
            </div>
         ))}
      </section>
   );
};

export default Slider;
