import React from 'react'

const STYLES =['normal--400','bold--700','lighter--100','bolder--800']
const SIZES = ['heading--small','heading--medium', 'heading--large']
const Title = (props) =>
{
    const { children, type, headingStyle, headingSize } = props;

    const checkTitleStyle = STYLES.includes(headingStyle) ? headingStyle : STYLES[0]
    const checkTitleSize = SIZES.includes(headingSize) ? headingSize : SIZES[0]


    return (
        <span className={`title ${checkTitleStyle} ${checkTitleSize}`}>
            {children}
        </span>
    )
}

export default Title
