import React, { useState, useEffect } from 'react';
import Title from '../Title/Title';

const Slider = ({ slides }) => {
   const [current, setCurrent] = useState(0);
   // const [active, setActive] = useState(0);
   // const { length } = slides;

   useEffect(() => {
      //   setCurrent((current ) % slides.length)
      setTimeout(
         () => setCurrent(current === slides.length - 1 ? 0 : current + 1),
         6000
      );
      return function () {
         clearTimeout(current % slides.length);
      };
   }, [current]);

   const handleSetClick = (event) => {
      // setCurrent(slides[event.target.getAttribute('data-slides')]);
      // setActive(event.target.getAttribute('data-slides'));
      console.log();
   };

   if (!Array.isArray(slides) || slides.length <= 0) return null;

   return (
      <section className='slider flex'>
         {slides.map((s, i) => (
            <div
               // if active slide, include the "active" class
               className={i === current ? 'slide  active' : 'slide '}
               key={s.title}
               // if not active, hide from screen readers for accessibility
               // aria-hidden={i !== current}
            >
               <div>
                  <div className='text--mb--s'>
                     <Title
                        headingStyle='bolder--800'
                        headingSize='heading--h2'
                     >
                        {s.title}
                     </Title>
                  </div>
                  <div className='text--mb--s'>
                     <Title
                        headingStyle='normal--400'
                        headingSize='heading--small'
                     >
                        {s.subtitle}
                     </Title>
                  </div>
                  <div className='flex'>
                     {Object.keys(slides).map((index, i) => (
                        <span
                           className={
                              current === i
                                 ? 'activeSlide flex active'
                                 : 'activeSlide'
                           }
                           onClick={(event) => handleSetClick(event)}
                           // data-slides={index}
                           key={index}

                        >
                           {'0'.concat(Number(index) + 1)}
                        </span>
                     ))}
                  </div>
               </div>
            </div>
         ))}
      </section>
   );
};

export default Slider;
