import React, {useEffect,useState} from 'react';

import Card from '../Card/Card.component';
import treasure from '../../assets/images/treasureHunt.png'

const Hero = () =>
{
    const [data, setData] = useState();
    const url = '../../src/assets/data/data.json'

    useEffect(() =>
    {
        const getData = async () =>
        {
            const response = await fetch(url);
            const data = await response.json();
            console.log(data);
            setData(data)
        };
        getData();
    }, [])
    return (
        <div className='hero'> 
            <Card
                image={treasure}
                title="Treasure Hunt"
                description="Final project in HackYourFuture. A Treasure Hunt game for kids, developed with an idea from CodeArt during an 8-week period, where we planned, designed, developed and deployed the game using Reactjs."
                goto="https://github.com/ghofranebenhmaid/fp-class12"
            />
        </div>
    )
}

export default Hero
