import React, { useEffect, useState } from 'react';
import Card from '../Card/Card.component';


const Repositories = () =>
{
    const [isLoading, setIsLoading] = useState(true);
    const [repositories, setRepositories] = useState([]);

    useEffect(() => {
        (async () =>
        {
      const url = 'https://api.github.com/users/ghofranebenhmaid/repos';

         setIsLoading(true);
         const response = await fetch(url);
         const { repositories: jsonResponse } = await response.json();
         setRepositories(jsonResponse);
         console.log(repositories);
         setIsLoading(false);
      })();
    }, []);

    return (
        <div>
            {isLoading && 'Loading...' }
         {!isLoading && repositories.length === 0 && 'Sorry, nothing found.'}
            {tiles.length > 0 &&
            tiles.map((data, index) => (
               <Card
                  key={index}
                  image={data.image}
                  title={data.title}
                  discription={data.discription}
                  goto={data.href}
               />
            ))}
        </div>
    )
}

export default Repositories
