   const [current, setCurrent] = useState(0);
   const { length } = slides;

   const [state, dispatch] = useReducer(carouselReducer, initialCarouselState);


const initialCarouselState = {
offset: 0,
desired: 0,
active: 0
};

 useEffect(() => {

     setTimeout( ()=> setCurrent((current + 1) % slides.length), 3000);

}, [current]);

   // const goToNext = () => {
   //    setCurrent(current === length - 1 ? 0 : current + 1);
   // };


   // useEffect(() => {
   //     setTimeout(goToNext, 3000);
   //    return function () {
   //       clearTimeout(goToNext);
   //    };
   // });

   if (!Array.isArray(slides) || length <= 0) return null;

   return (
      <section className='slider flex'>
         {slides.map((s, i) => (
            <div
               // if active slide, include the "active" class
               className={i === current ? 'slide active' : 'slide'}
               key={s.title}
               // if not active, hide from screen readers for accessibility
               aria-hidden={i !== current}
            >
               <div>
                  <div className='text--mb--s'>
                     <Title
                        headingStyle='bolder--800'
                        headingSize='heading--h2'
                     >
                        {s.title}
                     </Title>
                  </div>
                  <div>

                  <Title
                     headingStyle='normal--400'
                     headingSize='heading--small'
                     >
                     {s.subtitle}
                  </Title>
                  </div>

                        {/* <div >{ index }</div>  */}


                 </div>


            </div>
         ))}
      </section>
   );
};

export default Slider;
