import React,{useEffect,useState,useRef} from 'react';
import Button from '../Button/Button';
import Title from '../Title/Title';
import { Expo } from 'gsap';

const Card = (props) => {
   const { image, title, goto } = props;

   function useMousePosition() {
   let [mousePosition, setmousePosition] = useState({ x: null, y: null });

   useEffect(() => {
      function handlePosition(e) {
         setmousePosition({ x: e.pageX, y: e.pageY });
      }
      window.addEventListener('mousemove', handlePosition);
      return () => window.addEventListener('mousemove', handlePosition);
   }, []);
   return mousePosition;
}
   const container = useRef(null);

    let list = useRef();
   const [hoverState, setHoverState] = useState(false);
   const { x, y } = useMousePosition();
   const [listPosition, setListPosition] = useState({
      top: 0,
      left: 0,
   });

   useEffect(() => {
      setListPosition({
         top: list.current.getBoundingClientRect().top,
         left: list.current.getBoundingClientRect().left,
      });
   }, []);
   return (
      <div ref={list} className='card'>
         <div className='card__img mb--l'
            onHoverStart={() => setHoverState(true)}
            onHoverEnd={() => setHoverState(false)}
         ref={container}

         >
            <img src={`${image}`} alt={title} />
         </div>
          <div className='card__text' initial={{ opacity: 0 }}
            animate={{
               opacity: hoverState ? 1 : 0,
               x: x - listPosition.left,
               y: y - listPosition.top,
            }}>

            <div>

               {!!title && (
                <Title headingStyle='bolder--800' headingSize='heading--h4'>
                  {title}
               </Title>
               )}
            </div>

            <a href={`${goto}`}></a>

            {/* <Button
               type='button'
               buttonStyle='btn--simple'
               buttonSize='btn--simple'
            >
               See WebSite
            </Button> */}
         </div> 
      </div>
   );
};

export default Card;
