import React, {useEffect,useState} from 'react';

import Card from '../Card/Card.component';


const Hero = () =>
{



    useEffect(() =>
    {(async () =>
    {
        setIsLoading(true)
            const response = await fetch('https://bitbucket.org/ghofranebenhmaid/ghofrane.dev/raw/91832b79902169b4dd878b33425a4394df91a26f/src/assets/data/data.json');
        const {tiles: jsonResponse} = await response.json();

            console.log(jsonResponse);
        setTiles(jsonResponse)
        setIsLoading(false)

        })();

    }, [])
    return (
        <div className='hero'> 
            {isLoading && 'Loading...' }
            {(!isLoading && tiles.length === 0) && 'Sorry, nothing found.'}
            {(tiles.length > 0) &&
                tiles.map(((data, index) => (
                     <Card
                        key={ index }
                        image={ data.image }
                        title={ data.title }
                        description={ data.description }
                        goto={data.href}
                    />
                )


                    )) }
        </div>
    )
}

export default Hero
