import React, { useEffect, useState } from 'react';
import Card from '../Card/Card.component';


const Repositories = () =>
{
    const [isLoading, setIsLoading] = useState(true);
    const [repositories, setRepositories] = useState([]);


    const fetchRepositories = () => {
      const url = 'https://api.github.com/users/ghofranebenhmaid/repos';
      fetch(url)
         .then((res) => res.json())
         .then((data) => {
            console.log(data);

            setRepositories(data);
         })
         .catch((error) => console.log(error));
   };

   useEffect(() => {
      fetchRepositories();
   }, []);

    useEffect(() => {
        (async () =>
        {
      const url = 'https://api.github.com/users/ghofranebenhmaid/repos';

         setIsLoading(true);
         const response = await fetch('https://api.github.com/users/ghofranebenhmaid/repos');
         const { repositories: jsonResponse } = await response.json();
         setRepositories(jsonResponse);
         console.log(repositories);
         setIsLoading(false);
      })();
    }, []);

    return (
        <div >
            {isLoading && 'Loading...' }

        </div>
    )
}

export default Repositories
