import React, {useEffect,useState} from 'react';

import Card from '../Card/Card.component';
import treasure from '../../assets/images/treasureHunt.png'

const Hero = () =>
{
     const [isLoading, setIsLoading] = useState(false);
    const [data, setData] = useState();


    useEffect(() =>
    {(async () =>
        {
            const response = await fetch('https://bitbucket.org/ghofranebenhmaid/ghofrane.dev/raw/91832b79902169b4dd878b33425a4394df91a26f/src/assets/data/data.json');
        const data = await response.json();

            console.log(data);
            setData(data)
        });

    }, [])
    return (
        <div className='hero'> 
            <Card
                image={treasure}
                title="Treasure Hunt"
                description="Final project in HackYourFuture. A Treasure Hunt game for kids, developed with an idea from CodeArt during an 8-week period, where we planned, designed, developed and deployed the game using Reactjs."
                goto="https://github.com/ghofranebenhmaid/fp-class12"
            />
        </div>
    )
}

export default Hero
