import React, { useState,useEffect , useRef } from 'react';

import { AiOutlineRight, AiOutlineLeft } from 'react-icons/ai';



const Carousel = () => {

    const [slides] = useState([
        {
            source: "https://picsum.photos/1300",
            title: "Half Moon Pier"
        },
        {
            source: "https://picsum.photos/1350",
            title: "Port Washington Rocks"
        },
        {
            source: "https://picsum.photos/1400",
            title: "Abandoned Rail"
        }
    ]);

    // SET CAROUSEL DEFAULTS
    let [currentPosition, setCurrentPosition] = useState(0); // Initial slide index value
    let currentSlide = slides[currentPosition]; // variable index value we can reference later


    const [active, setActive] = useState(0);

    const dotRef = useRef()


    const arrowRightClick = () => {
        currentPosition !== slides.length -1 ? // Check index length
        setCurrentPosition(currentPosition + 1) : setCurrentPosition(currentPosition = 0);
        currentSlide = slides[currentPosition];
    }

    const arrowLeftClick = () => {
        currentPosition !== 0 ? // Check index length
        setCurrentPosition(currentPosition - 1) : setCurrentPosition(currentPosition = slides.length - 1);
        currentSlide = slides[currentPosition];

    }
    const handleSetClick = event => 
   {
         setCurrentPosition(slides[event.target.getAttribute('data-slides')])
        setActive(event.target.getAttribute('data-slides'))
        console.log(event.target);


    }


//     const goToNext = () => {
//       setCurrentPosition(currentPosition === slides.length - 1 ? 0 : currentPosition + 1);
//    };

//    useEffect(() => {
//        setTimeout(goToNext, 3000);
//     //    return function () {
//     //       clearTimeout(goToNext);
//     //    };
//    },[currentSlide]);



    return (
        <div className="carousel-block ">
            <div className="flex-container">

                <div id="slider-list">
                    <div className="slide">
                        <img src={currentSlide.source} alt={currentSlide.title} title={currentSlide.title} className="slider-img" />
                        <div className="arrows">
                            <div id="arrow-left" onClick={arrowLeftClick}><AiOutlineLeft/></div>
                            <div id="arrow-right" onClick={arrowRightClick}> <AiOutlineRight/></div>
                        </div>
                  <div className='flex' >

                            { Object.keys(slides).map(index => (



                      <span 

                                     onClick={ event => handleSetClick(event) }
                                    ref = {dotRef}
                     data-slides={ index }
                     key={index }
                     className={slides[index] === active ? 'activeSlide flex active1' : 'activeSlide flex '}
                     >{ '0'.concat(Number(index) + 1 ) }
                                    </span>

                     ))}

                     </div>

                    </div>

                </div>
            </div>
        </div>
    )
}

export default Carousel;
