import React,{ useEffect } from 'react'
import { useLocation } from 'react-router-dom';

const ScrollToTop = () =>
{
    const { pathname } = useLocation();


    useEffect(() => {
      // window.scrollTo(0, 0);
      window.scroll({
         top: 0,
         left: 0,
         behavior: 'smooth',
      });
   }, [pathname]);
   return null;
}

export default ScrollToTop
