import gsap from "gsap";

// adds serves image once you hover on
export const handleServes = (serves, target) => {
  gsap.to(target, {
    duration: 0,
    background: `url(${serves}) center center`
  });
  gsap.to(target, {
    duration: 0.4,
    opacity: 1,
    ease: "power3.inOut"
  });
  gsap.from(target, {
    duration: 0.4,
    skewY: 2,
    transformOrigin: "right top"
  });
};

// Removes the serves image once you hover off
export const handleServesReturn = target => {
  gsap.to(target, {
    duration: 0,
    skewY: 0
  });
  gsap.to(target, {
    duration: 0.4,
    opacity: 0,
    skewY: 0
  });
};

// Hover on the link
export const handleHover = (e) => {
   gsap.to(e.target, {
      duration: 0.3,
      y: 3,
      skewX: 10,
      ease: 'power1.inOut',
   });
};

// Hover off the link
export const handleHoverExit = (e) => {
   gsap.to(e.target, {
      duration: 0.6,
      y: 3,
      skewX: 0,
      ease: 'power1.inOut',
   });
};
