import React, { useState, useEffect } from 'react'
import ReactDOM from 'react-dom'
import Card from '../Card/Card.component';

import dataCard from '../../assets/data/data.json'

const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [data, setData] = useState([]);

useEffect( async () => {
    setLoading(true);
     fetch(dataCard)
      .then((response) => response.json())
      .then((data) => {
        setLoading(false);
          setData(data);
          console.log(data);
      })
      .catch((e) => {
        setLoading(false);
        setError('fetch failed');
      });
}, []);

const Hero = () => {
    return (
        <div className='hero'>
            <Card>
                {loading && 'Loading...'}
        {(!loading && data.length === 0) && 'Sorry, nothing found.'}
            </Card>
        </div>
    )
}

export default Hero
