import React, {useEffect,useState} from 'react';

import Card from '../Card/Card.component';


const Tiles = () =>
{
     const [isLoading, setIsLoading] = useState(false);
    const [tiles, setTiles] = useState([]);


    useEffect(() =>
    {(async () =>
    {
        setIsLoading(true)
            const response = await fetch('https://bitbucket.org/ghofranebenhmaid/ghofrane.dev/raw/15ebd0b06f858dc1359fb902571d9b00ff970861/src/assets/data/data.json');
        const jsonResponse = await response.json();

        setTiles(jsonResponse)
        setIsLoading(false)

        })();

    }, [])
    return (
        <div className='tiles container'> 
            {isLoading && 'Loading...' }
            {(!isLoading && tiles.length === 0) && 'Sorry, nothing found.'}
            {(tiles.length > 0) &&
                tiles.map(((data, index) => (
                     <Card
                        key={ index }
                        image={ data.image }
                        title={ data.title }
                        discription={ data.discription }
                        goto={data.href}
                    />
                )   
                    )) }
        </div>
    )
}

export default Tiles
