import React from 'react'
import Button from '../Button/Button';
const Card = (props) =>
{
     const { image, title, discription, goto } = props;
    return (
        <div className='card'>
            <div className='card__img'>
                <img src={ `${image }`} alt={ title }/>
            </div>
            <div className='card__text' >   
                <h1 className='text--mb'>{ title }</h1>   
                    {!!discription && (
					<p className='text--mb'>{discription}</p>
                ) }
                <a href={`${goto}`}></a>
                <Button
                    type='button'
                    buttonStyle= 'btn--primary--solid'
                    buttonSize='btn--medium'> See WebSite </Button>
            </div>
        </div>
    )
}

export default Card
