import React, { useRef,useState,useEffect}from 'react';
import Title from '../Title/Title';
import { Expo } from 'gsap';

import useMousePosition from '../../assets';

const Card = (props) => {
   const { image, title, goto } = props;

   let list = useRef();
   const [hoverState, setHoverState] = useState(false);
   const { x, y } = useMousePosition();
   const [listPosition, setListPosition] = useState({
      top: 0,
      left: 0,
   });


   useEffect(() => {
      setListPosition({
         top: list.current.getBoundingClientRect().top,
         left: list.current.getBoundingClientRect().left,
      });
   }, []);

   return (
      <div className='card'>
         <a href={`${goto}`}>
            <div
               className='card__img mb--l'
            onHoverStart={() => setHoverState(true)}
            onHoverEnd={() => setHoverState(false)}

            // ref={container}
            >
               <img src={`${image}`} alt={title} />
            </div>
            <div className='card__text'>
               <div
                initial={{ opacity: 0 }}
            animate={{
               opacity: hoverState ? 1 : 0,
               x: x - listPosition.left,
               y: y - listPosition.top,
            }}
            transition={{
               // ease: Power3.easeOut,
               ease: Expo.easeOut,
               duration: 0.8,
            }}
               >
                  {!!title && (
                     <Title
                        headingStyle='bolder--800'
                        headingSize='heading--h4'
                     >
                        {title}
                     </Title>
                  )}
               </div>
            </div>
         </a>
      </div>
   );
};

export default Card;
